import logo from './logo.svg';
import './App.css';
import './AlertApp.css';
import React, {useEffect, useState, useMemo} from "react";
import MainPage from './MainPage'
import Lists from "./Lists";
import DATA from "./InMemoryApp";
import Alert from './Alert'



function App() {
  //responsible for everyhing
  // rendering desired information
    const [showAlert, setShowAlert] = useState(false);
    let [showListAlert, setListAlert] = useState(false);
    // let [showListItemAlert, setListItemAlert] = useState(false);
    let [selectedList, setList] = useState(-1);
    let [data, setData] = useState(DATA);

    function handleAlertOK() {
        console.log('the frob should be blitzened here');
    }

    function toggleModal(){
        setShowAlert(false);
    }
    if (selectedList >= 0){
        return (
            <div>
                <div>
                    <Lists data={data} selectedId={selectedList}/>
                </div>
                {showListAlert && <Alert onClose={toggleModal} onOk={handleAlertOK} cancelName={"Don't Frob"} okName={"Frob"}>
                    <div>
                    Add Task?
                    </div>
                </Alert>}
            </div>
        )
    } else {
        return (
            <div>
                <div>
                <MainPage setData={setData} data={data} onListClick={(n) => setList(n)}/>
                </div>
                    showListAlert && <Alert onClose={toggleModal} onOk={handleAlertOK} cancelName={"Don't Frob"} okName={"Frob"}>
                <div>
                    Add List?
                </div>
            </Alert>}
            </div>)
    }
}

export default App;
