import "./main.css"
import {Data} from "./InMemoryApp"
import React, {useEffect, useState, useMemo} from "react";


function handleDeleteCheck(props){
     setTimeout(() => {
         props.handleDelete(props.data[props.selectedId].listItems.filter(listItemName => !listItemName.includes(props.listitem)))
     }, 1000)
}

function ListsItemDisplay(props){
    return (
    <div className="boxes">
        <input onChange={() => handleDeleteCheck(props)} type="checkbox" />
        <label>{props.listitem}</label><br/>
    </div>
    )
}

function Lists(props)
{
    return (
        <div>
            <h1>{props.data[props.selectedId].title}</h1>
            {props.data[props.selectedId].listItems.map((x) => <ListsItemDisplay selectedId={props.selectedId} handleDelete={props.handleDelete} data={props.data} listitem={x}/>)}
            <div id="button1">
                <button onClick={() => {props.setShowAlert(true)}} id="addTask">
                    <img src="plus_best.png"/>
                    <span>Add Task</span>
                </button>
            </div>
        </div>
    )
}

export default Lists;
