import "./main.css"
import React, {useEffect, useState, useMemo} from "react";
import Alert from "./Alert";
import {useCollection} from "react-firebase-hooks/firestore";
import {collection, doc, setDoc, where, getDoc, getDocs} from "firebase/firestore";
import firebase from "firebase/compat";

function ShowEditAlert(props) {
    props.setCurrentTask(props.listitem.id)
    props.setShowEditAlert(true)
}

function ListsItemDisplay(props){
    console.log(props.currentTasks);
    const [checked, setChecked] = useState(props.completed); //useState(props.listitem.completed)
    const [showAlert, setShowAlert] = useState(false);

    function handleAlertOKListItem(listitem, priority) {
        props.updateTask(props.list.id, props.id, listitem, props.completed, priority)
        // props.listitem.text = listItemText;
        // props.setData(Object.assign([], props.data))
    }
    function handleDelete(e) {
        e.stopPropagation()
        props.deleteTask(props.list.id, props.id)
        // props.list.listItems = props.list.listItems.filter((item) => item.id !== props.id)
        // props.setData(Object.assign([], props.data))
    }
    function showPriorityImage(priority){
        if (priority == "high"){
            return (
                <div>
            <img className="edit-delete-button" onClick={() => setShowAlert(true)} src={"exclamation-solid.svg"}></img>
            <img className="edit-delete-button" onClick={() => setShowAlert(true)} src={"exclamation-solid.svg"}></img>
                </div>)
        } else if (priority == "medium"){
            return (
                <div>
                    <img className="edit-delete-button" onClick={() => setShowAlert(true)} src={"exclamation-solid.svg"}></img>
                </div>
            )
        } else {
            return (
                <div>
                </div>
            )
        }
    }

    return (
        <div id="box1" className="boxes boxes-blue">
            <input checked={checked} type="checkbox" onChange={() => {
                props.updateTask(props.list.id, props.id, props.listitem, !props.completed, props.priority)
                // props.setData(Object.assign([], props.data))
                console.log("add completed attribute")
            }}/>
            <label>{props.listitem}</label><br/>

            <div className={"edit-delete-button-container"}>
                {showPriorityImage(props.priority)}
            <img className="edit-delete-button" onClick={() => setShowAlert(true)} src={"edit-solid.svg"}></img>
            <img className="edit-delete-button" onClick={(e) => handleDelete(e)} src={"times-solid.svg"}></img>
            </div>
            <Alert priority={props.priority} task={true} visible={showAlert} inputValue={props.listitem} onClose={() => setShowAlert(false)} onOk={handleAlertOKListItem} cancelName={"Don't Edit Task"} okName={"Edit Task"}>
                <div>Edit Task:</div>
            </Alert>
        </div>
    )
}

function Lists(props) {
    const [showAlert, setShowAlert] = useState(false);
    const [currentTasks, setTasks] = useState([]);
    const query = props.db.collection("lewing-hmc-tasks2");
    const [value, loading, error] = useCollection(query)
    let currTasks = [];
    let arr2 =[];
    if(!loading){
        getDocInfo().then((tasks) => currTasks = tasks);
        arr2 = currTasks
    }

    async function getDocInfo() {
        const q = query.doc(props.list.id).collection(props.list.id);
        const querySnapshot = await getDocs(q);
        querySnapshot.forEach((doc) => {
            currTasks.push([doc.data().id, doc.data().title, doc.data().completed, doc.data().priority])
        })
        return currTasks;
    }

    return (
        <>
            <h1>{props.data[0].title}</h1>
            {console.log("This is ?", arr2[0])}

            {arr2.map((x) => <ListsItemDisplay deleteTask={props.deleteTask} updateTask={props.updateTask} setTasks={setTasks} currentTasks={currTasks} list={props.list} setData={props.setData} data={props.data} id={x[0]} listitem={x[1]} completed={x[2]} priority={x[3]}/>)}

            {/*{currTasks.filter((y) => !y[2]).filter((z) => z[3] == "high").map((x) => <ListsItemDisplay deleteTask={props.deleteTask} updateTask={props.updateTask} setTasks={setTasks} currentTasks={currTasks} list={props.list} setData={props.setData} data={props.data} id={x[0]} listitem={x[1]} completed={x[2]} priority={x[3]}/>)}*/}
            {/*{currTasks.filter((y) => !y[2]).filter((z) => z[3] == "medium").map((x) => <ListsItemDisplay deleteTask={props.deleteTask} updateTask={props.updateTask} setTasks={setTasks} currentTasks={currTasks} list={props.list} setData={props.setData} data={props.data} id={x[0]} listitem={x[1]} completed={x[2]} priority={x[3]}/>)}*/}
            {/*{currTasks.filter((y) => !y[2]).filter((z) => z[3] == "low").map((x) => <ListsItemDisplay deleteTask={props.deleteTask} updateTask={props.updateTask} setTasks={setTasks} currentTasks={currTasks} list={props.list} setData={props.setData} data={props.data} id={x[0]} listitem={x[1]} completed={x[2]} priority={x[3]}/>)}*/}
            <hr/>
            <h3>Completed:</h3>
            {currentTasks.filter((y) => y[2]).map((x) => <ListsItemDisplay deleteTask={props.deleteTask} updateTask={props.updateTask} setTasks={setTasks} currentTasks={currentTasks} list={props.list} setData={props.setData} data={props.data} id={x[0]} listitem={x[1]} completed={x[2]} priority={x[3]}/>)}
            <div id="button1">
                <button onClick={() => {setShowAlert(true)}} className="addTask">
                    <img src="plus-solid.svg"/>
                    <span>Add Task</span>
                </button>
            </div>
            <Alert priority={props.priority} task={true} visible={showAlert}  onClose={() => setShowAlert(false)} onOk={(input) => props.addListItem(props.list, input, "low") } cancelName={"Don't Add Task"} okName={"Add Task"}>
                <div>Add Task:</div>
            </Alert>
        </>
    )
}

export default Lists;
