import "./main.css"
import {Data} from "./InMemoryApp"
import React, {useEffect, useState, useMemo} from "react";

// function handleToggle()

function handleDeleteCheck(props, checked, id){
    checked(true)
     setTimeout(() => {
         props.data[props.selectedId].listItems[id].completed = true;
         console.log( props.data)
     }, 500)
}

function ListsItemDisplay(props){
    let [isChecked, setChecked] = useState(props.listitem.completed)
    return (
        <div id="box1" className="boxes boxes-blue">
            <input onChange={() => setChecked(true)}
                   type="checkbox"/>
            <label>{props.listitem.text}</label><br/>
            <img className="edit-button" onClick={() => props.setShowAlert(true)} src={"edit-solid.svg"}></img>
        </div>
    )
}

function Lists(props)
{
    console.log(props.data, props.selectedId, props.data[props.selectedId])
    return (
        <>
            <h1>{props.data[props.selectedId].title}</h1>
            {
                props.data[props.selectedId].listItems
                    .filter((x) => props.data[props.selectedId].listItems.completed)
                    .map( <ListsItemDisplay setData={props.setData} setShowAlert={props.setShowAlert} selectedId={props.selectedId} handleDelete={props.handleDelete} data={props.data} listitem={x}/>)}
            <hr/>
            {props.data[props.selectedId].listItems.map((x) => <ListsItemDisplay checked={true} setData={props.setData} setShowAlert={props.setShowAlert} selectedId={props.selectedId} handleDelete={props.handleDelete} data={props.data} listitem={x}/>)}
            <div id="button1">
                <button onClick={() => {props.setShowAlert(true)}} className="addTask">
                    <img src="plus-solid.svg"/>
                    <span>Add Task</span>
                </button>
            </div>
        </>
    )
}

export default Lists;
